const initialState= {
    currentUser: null
}

const userReducer=(currentState= initialState, action) => {
    switch(action.type){
        case 'SET_CURRENT_USER':
            return {
                ...currentState,
                currentUser: action.payload
            }
    }
}

export default userReducer
