import { takeLatest, put, call, all } from 'redux-saga/effects'

import userActionTypes from './user-action-types'
import { googleSignInSuccess, googleSignInFailure } from './user-actions'

import { signInWithGoogle, createUserProfileInDB } from '../../firebase/firebase-utils'

export function* googleSignIn(){
    const userAuth= yield signInWithGoogle()
    const userRef= yield createUserProfileInDB(userAuth)
    const snapshot= yield userRef.get()
    console.log(snapshot)
    put(googleSignInSuccess({
        id: userAuth.id,
        ...userAuth.data()
    }))
}

export function* googleSignInStart(){
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, googleSignIn)
}

export function* userSagas(){
    yield all([call(googleSignInStart)])
}
