import React, { Component } from 'react'
import { Switch, Route } from 'react-router-dom'

import './App.css'

import Homepage from './pages/hompeage/homepage-component'
import Shop from './pages/shop/shop-component'
import Header from './components/header/header-component'
import SignInSignUp from './pages/signin-and-signup/signin-and-signup-component'
import { auth, createUserProfileInDB } from './firebase/firebase-utils'


class App extends Component {

  constructor(){
    super()

    this.state={
      currentUser: null
    }
  }

  unSubscribeFromAuth= null

  componentDidMount(){
    this.unSubscribeFromListener= auth.onAuthStateChanged(async userAuth => {
      if(userAuth){
        const userRef= await createUserProfileInDB(userAuth)
        userRef.onSnapshot(snapShot => {
          this.setState({
            currentUser: {
              id: snapShot.id,
              ...snapShot.data()
            }
          })
        })
      }
      else{
        this.setState({
          currentUser: null
        })
      }
    })
  }

  componentWillUnmount(){
    this.unSubscribeFromListener()
  }

  render(){
    return (
      <div>
        <Header currentUser={this.state.currentUser} />
        <Switch>
          <Route exact path='/' component={Homepage} />
          <Route path='/shop' component={Shop} />
          <Route path='/signin' component={SignInSignUp} />
        </Switch>
      </div>
    );
  }

}

export default App
